import React, { useState } from "react";
import {
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
  toast,
  ToastContainer,
} from "mdb-react-ui-kit";
import axios from 'axios'

export default function AddNode() {
  const [AddNodeModal, setAddNodeModal] = useState(false);
  const [NodeIp, setNodeIp] = useState('');
  const [NodePort, setNodePort] = useState(0);

  const toggleAddNodeModal = () => setAddNodeModal(!AddNodeModal);

  const notify = type => {
    return () => {
      switch (type) {
        case 'info':
          toast.info('Info message', {
            closeButton: false
          });
          break;
        case 'success':
          toast.success('Success message', {
            closeButton: false
          });
          break;
        case 'warning':
          toast.warn('Warning message', {
            closeButton: false
          });
          break;
        case 'error':
          toast.error('Error message', {
            closeButton: false
          });
          break;
        default:
          toast.error('Error message', {
            closeButton: false
          });
      }
    };
  };

  const AddNode = () => {
      axios.post('https://chia-node-list-api.vercel.app/node', { NodeIp, NodePort }).then(res => {
          const resp = JSON.parse(res)
          if (resp.success) {
              setNodeIp('')
              setNodePort(0)

          }
      })
  }

  return (
    <>
      <div className="text-end">
        <MDBBtn onClick={toggleAddNodeModal}>Add Node</MDBBtn>
      </div>
      <MDBModal
        show={AddNodeModal}
        getOpenState={(e: any) => setAddNodeModal(e)}
        tabIndex="-1"
      >
        <MDBModalDialog>
          <MDBModalContent>
            <MDBModalHeader>
              <MDBModalTitle>Add Node</MDBModalTitle>
              <MDBBtn
                className="btn-close"
                color="none"
                onClick={toggleAddNodeModal}
              ></MDBBtn>
            </MDBModalHeader>
            <MDBModalBody>
              <label htmlFor="NodeIp" className="font-weight-light">
                Node Ip
              </label>
              <input
                type="text"
                id="NodeIp"
                className="form-control"
                onChange={(e) => {
                  setNodeIp(e.target.value);
                }}
              />
              <br />
              <label htmlFor="NodePort" className="font-weight-light">
                Node Port
              </label>
              <input
                type="number"
                id="NodePort"
                className="form-control"
                onChange={(e) => {
                  setNodePort(e.target.value);
                }}
              />
            </MDBModalBody>

            <MDBModalFooter>
              <MDBBtn color="danger" onClick={toggleAddNodeModal}>
                Close
              </MDBBtn>
              <MDBBtn onClick={AddNode}>Add Node</MDBBtn>
            </MDBModalFooter>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  );
}
